import React,{useEffect, useState} from 'react';
import {Form, Input, Button, Icon, Avatar, Card, Pagination } from 'antd';
import {useDispatch, useSelector} from 'react-redux';
import PostCard from '../components/PostCard';


const Home = () => {
  const dispatch = useDispatch();
  const {mainPosts} = useSelector(state => state.post);
  const {page, setPage} = useState(0);
  const onChangePage = (p) =>{
    console.dir(p);
  }
  return (
    <div style={{paddingTop:"10px"}}>
      { mainPosts.length !== 0 ?(
        <div style={{marginBottom:"10px"}}>
          <Pagination onChange={onChangePage} simple defaultCurrent={page} total={parseInt(mainPosts.length * 4.5)} />
        </div>
      ) : null }
      { mainPosts && mainPosts.map(post => <PostCard key={post.date} post={post}/>) }

    </div>
  )
}




export default Home
