import React,{useState, useEffect, useCallback, useRef} from 'react';
import {useSelector, useDispatch} from 'react-redux';
import {Card, Icon, Button, Avatar, Form, List, Input, Comment} from 'antd';
import PropTypes from 'prop-types';
import {FIX_POST} from '../reducers/post';

const PostCard = ({post}) => {
  //console.dir(post)
  const dispatch = useDispatch();
  const fixBtn = useRef(null);
  const {mainPosts, me} = useSelector(state => state.post);
  const [fix, setFix] = useState(true);
  const [fixText, setFixText] = useState(post.title);
  const [fixTitle, setFixTitle] = useState(post.text);

  const onFix = useCallback(e =>{
    if(me === post.me){
      setFix( pre=> !pre);
      if(!fix){
        dispatch({
          type:FIX_POST,
          data:{
            fixText,fixTitle
          }
        })
      }
    }
    // const id = post.me+post.date;
    // const index = mainPosts.findIndex(r=> r.id === id);
    // console.log(index);
    // dispatch({
    //   type:FIX_POST,
    //   data:index
    // })
  },[me, fix, fixText, fixTitle] );

  const onChangeFixTitle = useCallback(e=>{
    setFixTitle(e.target.value);
  },[]);
  const onChangeFixText = useCallback(e=>{
    setFixText(e.target.value);
  },[]);


  return(
    <div style={{margin:"15px", padding:15}}>
      <Card
       title={(
         <>
          { fix ? <div style={{width:"70%"}}>{post.title}</div> : <Input style={{width:"70%"}} value={fixTitle} onChange={onChangeFixTitle}/> }
          <div>{post.date}</div>
         </>
       )}
       key={post.date}
       extra={me  == post.me ? <Button ref={fixBtn} onClick={onFix}>수정</Button> : null}
      >
       <Card.Meta
         avatar={<Avatar>{post.me[0]}</Avatar>}
         description={ fix ? post.text : <Input.TextArea value={fixText} onChange={onChangeFixText} rows={4}/>}
       />
      </Card>
    </div>
  )
}

PostCard.propTypes = {
  post: PropTypes.shape({
    title:PropTypes.string,
    text:PropTypes.string,
    me:PropTypes.string
  })
}

export default PostCard
